mapper.delete:
DELETE
FROM incident_scnd_comment
WHERE id = #{id}
AND user_id = #{userId}
AND incident_comment_id = #{commentId}

mapper.update:
UPDATE
incident_scnd_comment
SET thumb_ups = thumb_ups + 1
WHERE id = #{iscId}
AND NOT EXISTS (
  SELECT
  id
  FROM isc_thumb_up
  WHERE user_id = #{userId}
  AND isc_id = #{iscId}
)

UPDATE
incident_scnd_comment
SET thumb_ups = thumb_ups - 1
WHERE id = #{iscId}
AND EXISTS (
  SELECT
  id
  FROM isc_thumb_up
  WHERE user_id = #{userId}
  AND isc_id = #{iscId}
)
