mapper.select:
SELECT

FROM user_notice
WHERE view = #{view}
AND user_id = #{userId,jdbcType=INTEGER}

mapper.update:
update user_notice
set view = 1
where user_id = #{userId,jdbcType=INTEGER}
AND content= #{content,jdbcType=VARCHAR}

mapper.select:
SELECT
COUNT(1)
FROM user_notice
WHERE
view = #{view}
AND user_id = #{userId,jdbcType=INTEGER}
AND follower_id = #{followerId,jdbcType=INTEGER}
